import React from 'react'
import styled from "styled-components";
import {
  space,
  SpaceProps,
  color,
  ColorProps,
  typography,
  TypographyProps,
  border,
  BorderProps,
  shadow,
  ShadowProps,
  layout,
  LayoutProps,
  position,
  PositionProps,
  compose
} from "styled-system";

const css = compose(space, color, typography, border, shadow, layout, position)

export interface StyledBoxProps extends SpaceProps,
ColorProps,
TypographyProps,
BorderProps,
ShadowProps,
LayoutProps,
PositionProps{}

const StyledBox = styled('div')<StyledBoxProps>`
  box-sizing: 'border-box';
  ${css}
`

interface BoxProps {
  as?: string
  children: React.ReactNode
}

function Box({children, as, ...rest}:BoxProps){
  return <StyledBox {...rest}>{children}</StyledBox>
}

Box.defaultProps = {
  fontFamily: 'robotoCondensed',
}

Box.displayName = 'Box'

export default Box
