import React from 'react'
import { Link as RRLink } from 'react-router-dom'
import Box from './Box'

const GenericLink = ({ to,label='cenas', ...rest }: { to: string,label?: string }) =>
  to && to.match(/^(https:\/\/*|http:\/\/*|mailto:*)/) ? (
    <a
      href={to}
      target="_blank"
      rel="noopener noreferrer"
      aria-label={label}
      {...rest}
    />
  ) : (
    <RRLink to={to} {...rest} />
  )

const Link = (props:any) => <Box {...props} />

Link.defaultProps = {
  as: GenericLink,
  color: 'text',
}

export default Link
