import 'whatwg-fetch'
import { useEffect, useState, useRef } from 'react'
import { API_URL } from '../utils/constants'
import { Character, CharactersData, UseCharacters } from './Comic'

export function useCharacters(characters:Character[]): UseCharacters{
  const [isLoading, setLoading] = useState(true)
  const [charactersImgs, setCharacter] = useState<CharactersData[]>([])
  // const charactersRef = useRef(characters)

  const urlsToFetch = characters.map(character => character.resourceURI.split(/public/g).pop())
  useEffect(() => {

    function getCharacters() {
      Promise.all(urlsToFetch.map( async (url) => {
        const response = await fetch(`${API_URL}${url}`)
        const data = await response.json()

        setCharacter((prevCharacter) => ([
          ...prevCharacter,
          ...data.data.results.map((result: any) => ({thumbnail:result.thumbnail, name:result.name}))
        ]))

      })).finally(() => {
        setLoading(false)
      })
    }
    getCharacters()
  }, [characters])

  return { isLoading, data: charactersImgs }
}
