import 'whatwg-fetch'
import { useEffect, useState } from 'react'
import { API_URL } from '../utils/constants'
import { Comic, UseComics, ApiResponse, Data } from './Comic'

export function useComics(): UseComics{
  const [isLoading,setLoading] = useState(true)
  const [offset, setOffset] = useState(0)
  const [comics, setComics] = useState<Data | []>()

  useEffect(() => {
    async function getComics() {
      const response = await fetch(`${API_URL}/comics?offset=${offset}`)
      const data = await response.json()

      // setComics((prevComics) => ([
      //   ...prevComics,
      //   ...data.data.results
      // ]))
      setComics((prevData) =>({
        ...data.data,
        data:{
          results:[
            ...prevData?.data.results,
            ...data.data.results,
          ]
        }
      }))
      setLoading(false)
    }
    getComics()
  }, [offset])

  function loadMore(){
    setOffset(20 + offset)
  }

  return { isLoading, data: comics, offset, loadMore }
}
