import {useEffect, useState} from 'react'

declare type ApiResponse = {
  offset:number,
  limit:number,
  total:number,
  results:[object],
}

export function useComics():ApiResponse{
  const [comics,setComics] = useState([])
  useEffect(() => {
    async function getComics() {
      const response = await fetch('http://localhost:8000/api/comics', { headers: { 'content-type': 'application/json' } })
      const data = await response.json()
      setComics(await data?.data)
    }
    getComics()
  }, [])
  return ApiResponse
}
