import React from 'react'
import Box, {BoxProps} from './Box'
import styled from 'styled-components'
import {variant} from 'styled-system'

type Variants = "primary" | "secondary"

interface StyledBtnProps extends BoxProps{
  variant?: Variants
}

const StyledBtn = styled(Box)<StyledBtnProps>(
  variant({
    variants: {
      primary: {
        borderColor: 'secondary',
        bg: 'transparent',
        color: 'text',
        textTransform: 'uppercase',
        '&:hover': {
          color: 'dark',
          bg: 'secondary',
        }
      },
      secondary: {
        borderWidth: 1,
        borderStyle: 'outline',
      },
    },
  })
)

StyledBtn.defaultProps = {
  paddingY: 1,
  paddingX: 5,
  fontWeight: 'bold',
  fontSize: 2,
  borderWidth: 2,
  borderStyle: 'outline',
  variant: 'primary',
}

interface ButtonProps {
  children: React.ReactNode
  isLoading?: Boolean
  onClick: (event: React.MouseEvent<HTMLButtonElement>) => void
}

function Button({isLoading,children, ...rest}:ButtonProps){
  return <StyledBtn {...rest} as="button">{children}</StyledBtn>
}


export default Button
