import React from 'react'
import styled from "styled-components";
import {
  space,
  SpaceProps,
  color,
  ColorProps,
  typography,
  TypographyProps,
  border,
  BorderProps,
  shadow,
  ShadowProps,
  layout,
  LayoutProps,
  position,
  PositionProps,
  compose
} from "styled-system";

const css = compose(space, color, typography, border, shadow, layout, position)

export type BoxProps = SpaceProps &
ColorProps &
TypographyProps &
BorderProps &
ShadowProps &
LayoutProps &
PositionProps

const Box = styled('div')<BoxProps>`
  box-sizing: 'border-box';
  ${css}
`

// type BoxProps {
//   as?: string
//   children: React.ReactNode
// }

// function Box({children, as, ...rest}:BoxProps){
//   return <StyledBox {...rest}>{children}</StyledBox>
// }

Box.defaultProps = {
  fontFamily: 'robotoCondensed',
}

Box.displayName = 'Box'

export default Box
