import styled from 'styled-components'
import { flexbox, FlexProps,  variant } from 'styled-system'
import Box from './Box'

type Variants = "container" | "item"

interface GridProps extends FlexProps {
  variant?: Variants
}

const Grid = styled(Box)<GridProps>(
  flexbox,
  variant({
    variants: {
      container: {
        display: 'flex',
      },
      item: {},
    },
  })
)


// Grid.defaultProps = {
//   display: 'flex',
// }

export default Grid
