import styled, { keyframes } from 'styled-components'

const rotate = keyframes`
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
`

const loading = keyframes`
0%{
    box-shadow: 0 -0.3em 0 -0.2em, 0 -0.3em 0 -0.22em, 0 -0.3em 0 -0.24em, 0 -0.3em 0 -0.26em, 0 -0.3em 0 -0.277em;
}
5%,
  95%{
    box-shadow: 0 -0.3em 0 -0.2em, 0 -0.3em 0 -0.22em, 0 -0.3em 0 -0.24em, 0 -0.3em 0 -0.26em, 0 -0.3em 0 -0.277em;
  }
10%,
  59%{
    box-shadow: 0 -0.3em 0 -0.2em, -0.087em -0.825em 0 -0.22em, -0.173em -0.812em 0 -0.24em, -0.256em -0.789em 0 -0.26em, -0.297em -0.775em 0 -0.277em;
  }
20%{
  box-shadow: 0 -0.3em 0 -0.2em, -0.338em -0.758em 0 -0.22em, -0.555em -0.617em 0 -0.24em, -0.671em -0.288em 0 -0.26em, -0.749em -0.34em 0 -0.277em;
  }
38%{
  box-shadow: 0 -0.3em 0 -0.2em, -0.377em -0.74em 0 -0.22em, -0.645em -0.522em 0 -0.24em, -0.775em -0.297em 0 -0.26em, -0.82em -0.09em 0 -0.277em;
  }
100%{
  box-shadow: 0 -0.3em 0 -0.2em, 0 -0.3em 0 -0.22em, 0 -0.3em 0 -0.24em, 0 -0.3em 0 -0.26em, 0 -0.3em 0 -0.277em;
  }
`

const Loader = styled('div')`
  color: #ffffff;
  font-size: 90px;
  text-indent: -9999em;
  overflow: hidden;
  width: 0.5em;
  height: 0.5em;
  border-radius: 50%;
  position: relative;
  transform: translateZ(0);
  animation: ${loading} 1.7s infinite ease, ${rotate} 1.7s infinite ease;
`

export default Loader
