import React from 'react'
// import styled from 'styled-components'
import RSelect, { ValueType, OptionTypeBase } from 'react-select'
import {colors, borderStyles, borderWidths, sizes, radii, space,fontSizes,lineHeights } from './theme'

// const StyledSelect = styled(RSelect)`
//   width: ${({ theme }) => `${theme.sizes['small']}`};
//   outline: none;
//   box-shadow: none;
//   border: ${({ theme }) => `${theme.borderWidths[1]} ${theme.borderStyles['outline']} ${theme.colors['secondary']}`};
//   border-radius:0;
// `

const styles = {
  control: () => ({
    // none of react-select's styles are passed to <Control />
    width:sizes["small"],
    paddingTop:space[1],
    paddingBottom:space[1],
    // paddingX:{2},
    fontSize:fontSizes[2],
    lineHeight:lineHeights[1],
    borderStyle:'outline',
    borderWidth:borderWidths[1],
    borderColor:colors["secondary"],
    backgroundColor:colors["transparent"],
    color:colors["text"],
    borderRadius:radii[0],
  }),
}

interface SelectProps {
  options: {label:string,value:string}[]
  onChange: (value:ValueType<OptionTypeBase>) => void
}

function Select({options,onChange, ...rest}:SelectProps){
  // const [state, setState] = useState('')
  // const onChangeMemo = useCallback(onChange,[])

  // useEffect(() => {
  //   onChangeMemo(state)
  // }, [state, onChangeMemo])

  const handleChange = (value: ValueType<OptionTypeBase>) => {
    // e.preventDefault()
    onChange(value)
  }

  return (
    <RSelect {...rest}
      onChange={handleChange}
      options={options}
      styles={styles}
      // as="select"
      // width="small"
      // paddingY={1}
      // paddingX={2}
      // fontSize={2}
      // lineHeight={1}
      // borderStyle='outline'
      // borderWidth={1}
      // borderColor="secondary"
      // backgroundColor="transparent"
      // color="text"
      // borderRadius={0}
    />
  )
}
export type { ValueType, OptionTypeBase }
export default Select
