import React from 'react'
import Box, {BoxProps} from './Box'
import styled from 'styled-components'
import {variant} from 'styled-system'

type Variants = "contained" | "outline"

interface StyledBtnProps {
  variant?: Variants
}

const StyledBtn = styled(Box)<StyledBtnProps>(
  {
    padding: `${(theme:any) => `${theme.space[2]}`};`,
  },
  variant({
    variants: {
      contained: {
        borderWidth: 1,
        borderStyle: 'contained',
      },
      outline: {
        borderWidth: 1,
        borderStyle: 'outline',
      },
    },
  })
)

StyledBtn.defaultProps = {
  variant: 'contained',
}

interface ButtonProps extends StyledBtnProps{
  sx: BoxProps
  children: React.ReactNode
  isLoading?: Boolean
  onClick: (event: React.MouseEvent<HTMLButtonElement>) => void
}

function Button({isLoading,children, ...rest}:ButtonProps){
  return <StyledBtn {...rest} as="button">{children}</StyledBtn>
}


export default Button
