import React from 'react'
import styled from "styled-components";
import {
  space,
  SpaceProps,
  color,
  ColorProps,
  typography,
  TypographyProps,
  border,
  BorderProps,
  shadow,
  ShadowProps,
  layout,
  LayoutProps,
  position,
  PositionProps,
  compose
} from "styled-system";

const css = compose(space, color, typography, border, shadow, layout, position)

export interface BoxProps extends SpaceProps,
ColorProps,
TypographyProps,
BorderProps,
ShadowProps,
LayoutProps,
PositionProps{}

const StyledBox = styled('div')<BoxProps>`
  box-sizing: 'border-box';
  ${css}
`

function Box(props:React.PropsWithChildren<P>){
  return <StyledBox {...props}>{props.children}</StyledBox>
}

Box.defaultProps = {
  fontFamily: 'robotoCondensed',
}

Box.displayName = 'Box'

export default Box
