import React from 'react'
import {useRouteMatch} from 'react-router-dom'
import { useComics } from '../../hooks'
import { Comic, UseComics } from '../../hooks/Comic'

import {Ul, Li} from '../UI/Lists'
import Link from '../UI/Link'
import Button from '../UI/Button'

function Master(){
  const { data, isLoading, loadMore }: UseComics = useComics()
  const { url } = useRouteMatch();

  if (!data.length && isLoading) return <span>Is Loading....</span>

  return (
    <>
      <Ul>
        {data.map(({ id, title }: Comic, idx) => (
          <Li key={`${idx}/${id}`}>
            <Link 
              fontSize={4}
              to={`${url}/${id}`}
            >{title}</Link>
          </Li>
        ))}
      </Ul>
      <Button sx={{bg:"secondary"}} onClick={() => loadMore()}>Load More</Button>
    </>
  )


}

export default Master
