import 'whatwg-fetch'
import { useEffect, useState } from 'react'
import { API_URL } from '../utils/constants'
import { Character,Image, UseCharacters } from './Comic'

export function useCharacters(characters:Character[]): UseCharacters{
  const [isLoading, setLoading] = useState(true)
  const [charactersImgs, setCharacter] = useState<Image[]>([])

  const urlsToFetch = characters.map(character => character.resourceURI.split(/public/g).pop())

  console.log('urlsToFetch', urlsToFetch)
  useEffect(() => {
    function getCharacters() {
      Promise.all(urlsToFetch.map( async (url) => {
        const response = await fetch(`${API_URL}${url}`)
        const data = await response.json()

        setCharacter((prevCharacter) => ([
          ...prevCharacter,
          ...data.data.results.map((result: any) => result.thumbnail)
        ]))
      }))

      setLoading(false)
    }
    getCharacters()
  }, [])

  return { isLoading, data: charactersImgs }
}
