import {useEffect, useState} from 'react'
import {API_URL} from '../utils/constants'

declare type Price = {
  price: number,
  type: string
}

declare type Image = {
  extension: string,
  path: string
}

declare type Url = {
  type: string,
  url: string
}

declare type Comic = {
  id: number,
  description: string,
  title: string,
  prices: [Price],
  images: [Image]
  urls: [URL]
}

declare type ApiResponse = {
  offset:number,
  limit:number,
  total:number,
  results:[Comic],
} | never[]

export function useComics():ApiResponse{
  const [comics,setComics] = useState([])
  useEffect(() => {
    async function getComics() {
      const response = await fetch(`${API_URL}/comics`, { headers: { 'content-type': 'application/json' } })
      const data = await response.json()
      setComics(await data?.data)
    }
    getComics()
  }, [])
  return comics
}
